class StructMeta:
    def __init_subclass__(cls, **kwds):
        super().__init_subclass__(**kwds)
        if not getattr(cls, 'no_struct_members', False):
            cls.descriptor = StructDescriptor(cls.__annotations__)
            for name, annot in cls.descriptor.annotations.items():
                setattr(cls, name, annot.default)
            cls.size = cls.descriptor.size
