class Registry(type):
    items = []
    def __new__(mcls, name, bases, ns):
        cls = super().__new__(mcls, name, bases, ns)
        Registry.items.append(cls)
        return cls

class Base(metaclass=Registry):
    pass

class A(Base): pass
class B(Base): pass

class RequireFields(type):
    def __new__(mcls, name, bases, ns):
        if "fields" not in ns:
            raise TypeError("Missing 'fields' attribute")
        return super().__new__(mcls, name, bases, ns)

class Model(metaclass=RequireFields):
    pass

class User(Model):
    fields = ["id", "name"]  # OK

class Bad(Model):
    pass  # Error: Missing 'fields'

class Field:
    def __init__(self):
        self.name = None

class Model:
    id = Field()
    id.name = "id"   # must be assigned manually

