def classify(x):
    if x == "yes":
        return 1
    elif x == "no":
        return 0
    elif x in ("maybe", "idk"):
        return -1
    else:
        return None

def handle(event):
    kind, payload = event
    if kind == "move" and len(payload) == 2:
        x, y = payload
        return f"MOVE {x} {y}"
    if kind == "quit":
        return "QUIT"
    return "UNKNOWN"

class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

def quad(p):
    if p.x >= 0 and p.y >= 0:
        return "Q1"
    if p.x < 0 and p.y >= 0:
        return "Q2"
    if p.x < 0 and p.y < 0:
        return "Q3"
    return "Q4"

def parity(n):
    if n % 2 == 0:
        return "even"
    else:
        return "odd"

def accumulate(xs):
    total = 0
    for x in xs:
        total += x
    return total
