# taken from the pygame wiki http://pygame.org/wiki/OBJFileLoader

class ObjFile:
    def __init__(self, file):
        """Loads a Wavefront OBJ file. """
        self.vertices = []
        self.normals = []
        self.texcoords = []
        self.faces = []

        material = None
        for line in file:
            if line.startswith('#'): continue
            values = line.split()
            if not values: continue
            if values[0] == 'v':
                v = map(float, values[1:4])
                self.vertices.append(v)
            elif values[0] == 'vn':
                v = map(float, values[1:4])
                self.normals.append(v)
            elif values[0] == 'vt':
                self.texcoords.append(map(float, values[1:3]))
            elif values[0] in ('usemtl', 'usemat'):
                material = values[1]
            elif values[0] == 'mtllib':
                pass
                #self.mtl = MTL(values[1])
            elif values[0] == 'f':
                verts= []
                for v in values[1:]:
                    vert = []
                    w = v.split('/')

                    vert.append(int(w[0]))

                    if len(w) >= 2 and len(w[1]) > 0:
                        vert.append(int(w[1]))
                    else:
                        vert.append(0)

                    if len(w) >= 3 and len(w[2]) > 0:
                        vert.append(int(w[2]))
                    else:
                        vert.append(0)
                    verts.append(vert)
                self.faces.append(verts)

# Class to read


class MeshPly:
    def __init__(self, filename, color=(0, 0, 0)):

        f = open(filename, "r")
        self.vertices = []
        self.colors = []
        self.indices = []
        self.normals = []

        vertex_mode = False
        face_mode = False

        nb_vertices = 0
        nb_faces = 0

        idx = 0

        with f as open_file_object:
            for line in open_file_object:
                elements = line.split()
                if vertex_mode:
                    elements = [float(e) for e in elements]
                    self.vertices.append(elements[:3])
                    self.normals.append(elements[3:6])

                    if elements[6:9]:
                        self.colors.append([i / 255 for i in elements[6:9]])
                    else:
                        self.colors.append([i / 255.0 for i in color])

                    idx += 1
                    if idx == nb_vertices:
                        vertex_mode = False
                        face_mode = True
                        idx = 0
                elif face_mode:
                    self.indices.append([float(i) for i in elements[1:4]])
                    idx += 1
                    if idx == nb_faces:
                        face_mode = False
                elif elements[0] == "element":
                    if elements[1] == "vertex":
                        nb_vertices = int(elements[2])
                    elif elements[1] == "face":
                        nb_faces = int(elements[2])
                elif elements[0] == "end_header":
                    vertex_mode = True


